import * as React from 'react';
import { Image, Platform, StyleSheet, Text, TouchableOpacity, View, Alert, ScrollView } from 'react-native';
import { Input } from 'react-native-elements';
import { TutorCard } from '../components/TutorCard';
import { image } from '../assets/images/spock.jpg';
import { Icon } from 'react-native-elements';



export default function SignUp({ navigation }) {

    return (
        <ScrollView style={styles.container} contentContainerStyle={styles.contentContainer}>
            <View style={styles.welcomeContainer}>
                <Text style={styles.welcomeText}>Sign Up!</Text>

                <Input
                    labelStyle={{ color: 'black' }}
                    containerStyle={{ paddingTop: 20, }}
                    label='Full name'
                    rightIcon={
                        <Icon
                            name='person'
                            size={24}
                            color='#d3d3d3'
                        />
                    }
                    placeholder='Enter name' />
                <Input
                    labelStyle={{ color: 'black' }}

                    containerStyle={{ paddingTop: 20, }}
                    label='Email'
                    rightIcon={
                        <Icon
                            name='mail'
                            size={24}
                            color='#d3d3d3'
                        />
                    }
                    placeholder='enter email' />
                <Input
                    labelStyle={{ color: 'black' }}

                    containerStyle={{ paddingTop: 20, }}
                    label='Create username'
                    rightIcon={
                        <Icon
                            name='code'
                            size={24}
                            color='#d3d3d3'
                        />
                    }
                    placeholder='enter username' />
                <Input
                    labelStyle={{ color: 'black' }}

                    containerStyle={{ paddingTop: 20, }}
                    label='City'
                    rightIcon={
                        <Icon
                            name='place'
                            size={24}
                            color='#d3d3d3'
                        />
                    }
                    placeholder='city' />
                <Input
                    labelStyle={{ color: 'black' }}

                    containerStyle={{ paddingTop: 20, }}
                    label='School/Faculty'
                    rightIcon={
                        <Icon
                            name='school'
                            size={24}
                            color='#d3d3d3'
                        />
                    }
                    placeholder='full name of institution' />
                <Input
                    labelStyle={{ color: 'black' }}

                    containerStyle={{ paddingTop: 20, }}
                    label='Create Password'
                    rightIcon={
                        <Icon
                            name='lock'
                            size={24}
                            color='#d3d3d3'
                        />
                    }
                    placeholder='Password' />
                <TouchableOpacity onPress={() => navigation.navigate('Home')} style={styles.signUp}>
                    <Text style={styles.signUpText}>Sign Up</Text>
                </TouchableOpacity>
            </View>



        </ScrollView>



    );
}




const styles = StyleSheet.create({

    container: {
        flex: 1,
        backgroundColor: '#fff',
    },
    contentContainer: {
        padding: 20,
        justifyContent: 'space-evenly',
    },
    welcomeContainer: {
        alignItems: 'stretch',
        marginBottom: 20,
    },
    welcomeText: {
        fontSize: 23,
        marginTop: 20,
        marginBottom: 10,
        marginLeft: 10,
        color: 'black',
        fontWeight: 'bold',
        textAlign: 'left',
    },

    signUp: {
        backgroundColor: 'rgba(255,71,105, 1)',
        alignItems: 'stretch',
        borderRadius: 10,
        marginTop: 5,
    },
    signUpText: {
        fontSize: 17,
        fontWeight: 'bold',
        color: 'white',
        textAlign: 'center',
        padding: 15,
    },
    categoriesContainer: {
        paddingTop: 10,
        paddingBottom: 10,
    },

    signUp: {
        backgroundColor: 'rgba(255, 71, 105, 1)',
        alignItems: 'stretch',
        borderRadius: 20,
        marginTop: 20,
    },
    signUpText: {
        fontSize: 17,
        fontWeight: 'bold',
        color: 'white',
        textAlign: 'center',
        padding: 15,
    },
    getStartedContainer: {
        alignItems: 'center',
        marginHorizontal: 50,
    },
    homeScreenFilename: {
        marginVertical: 7,
    },
    codeHighlightText: {
        color: 'rgba(96,100,109, 0.8)',
    },
    codeHighlightContainer: {
        backgroundColor: 'rgba(0,0,0,0.05)',
        borderRadius: 3,
        paddingHorizontal: 4,
    },
    getStartedText: {
        fontSize: 17,
        color: 'rgba(96,100,109, 1)',
        lineHeight: 24,
        textAlign: 'left',
    },
    tabBarInfoContainer: {
        position: 'absolute',
        bottom: 0,
        left: 0,
        right: 0,
        ...Platform.select({
            ios: {
                shadowColor: 'black',
                shadowOffset: { width: 0, height: -3 },
                shadowOpacity: 0.1,
                shadowRadius: 3,
            },
            android: {
                elevation: 20,
            },
        }),
        alignItems: 'center',
        backgroundColor: '#fbfbfb',
        paddingVertical: 20,
    },
    tabBarInfoText: {
        fontSize: 17,
        color: 'rgba(96,100,109, 1)',
        textAlign: 'center',
    },
    navigationFilename: {
        marginTop: 5,
    },
    helpContainer: {
        marginTop: 15,
        alignItems: 'center',
    },
    helpLink: {
        paddingVertical: 15,
    },
    helpLinkText: {
        fontSize: 14,
        color: '#2e78b7',
    },
    filterButtons: {
        padding: 3,
        backgroundColor: '#F3F3F3',
        borderColor: '#F3F3F3',
        borderRadius: 20,
        borderWidth: 1,
    },
    filterButtonsText: {
        padding: 5,
        paddingLeft: 25,
        paddingRight: 25,
    },


});
