import * as React from 'react';
import { Image, Platform, StyleSheet, Text, TouchableOpacity, View, Alert, ScrollView} from 'react-native';
import { Input } from 'react-native-elements';
import { TutorCard } from '../components/TutorCard';
import { image } from '../assets/images/spock.jpg';
import {Icon} from 'react-native-elements';
import { tutorsList } from '../tutors';
import { SafeAreaView } from 'react-native-safe-area-context';
import { ActivityIndicator } from 'antd-mobile';




export default function TutorList({navigation}) {
  const [isLoading, setLoading] = React.useState([]);
  const [tutors, setTutors] = React.useState([]);

  React.useEffect(() => {
    fetch('http://localhost:3000/dev/tutors')
      .then((response) => response.json())
      .then((responseJson) => {
        return responseJson.tutors;
      })
      .then(tutors => {
        setTutors(tutors);
        setLoading(false);
      })
      .catch(error => {
        console.error(error);
      })
      .finally(() => setLoading(false));

  }, []);



  return (
    <SafeAreaView>
      {isLoading ? <ActivityIndicator/> :(
    <View style={styles.container}>
      <ScrollView style={styles.container} contentContainerStyle={styles.contentContainer}>
        <View style={styles.welcomeContainer}>
          <Text style={styles.welcomeText}>Tutors</Text>
          <Input 
            onChange={(e) => {
              if (e.nativeEvent.text === "") {
                setTutors(tutorsList);
              } else {
                setTutors(
                  tutorsList.filter((t) => t.name === e.nativeEvent.text)
                );
              }
            }}
            inputContainerStyle={{borderBottomWidth:0}}
            rightIcon={
              <Icon
                name='search'
                size={24}
                color='black'
              />

            }
          containerStyle={{ backgroundColor:'#F3F3F3', borderRadius: 15}} placeholder='Search here...'/>

        <View style={{
        flex: 1,
        flexDirection: 'row',
        justifyContent: 'space-between',
        marginTop: 15, }}>

        <View>
          <TouchableOpacity onPress={() => Alert.alert('Hvala :D')} style={styles.filterButtons}> 
            <Text style={styles.filterButtonsText}>Algebra</Text> 
          </TouchableOpacity> 
        </View>
        <View>
          <TouchableOpacity onPress={() => Alert.alert('Hvala :D')} style={styles.filterButtons}> 
            <Text style={styles.filterButtonsText}>Coding</Text> 
          </TouchableOpacity> 
        </View>
        <View>
              <TouchableOpacity onPress={() => navigation.navigate('Filters')} style={styles.filterButtons}> 
            <Text style={styles.filterButtonsText}>Filters</Text> 
          </TouchableOpacity> 
        </View>

       </View>
      </View>


       <TouchableOpacity onPress={() => {
         navigation.navigate('PublicProfile', { 
           name: tutors.name,
           email: tutors.email,
           bio: tutors.bio,
           price: t.price,
           faculty: t.faculty,
           location: t.location,
         })
       }}>

       <TutorCard image={t.imgSrc} name={t.name} description={t.description}></TutorCard>
       </TouchableOpacity>
       ))}

      </ScrollView>


    </View>
      )}
    </SafeAreaView>
  );
}

TutorList.navigationOptions = {
  header: null,
};



const styles = StyleSheet.create({

  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  developmentModeText: {
    marginBottom: 20,
    color: 'rgba(0,0,0,0.4)',
    fontSize: 14,
    lineHeight: 19,
    textAlign: 'center',
  },
  contentContainer: {
    padding: 20,
    justifyContent: 'space-evenly',
  },
  welcomeContainer: {
    alignItems: 'stretch',
    marginBottom: 20,
  },
  welcomeText: {
    fontSize: 23,
    marginBottom: 10,
    color: 'black',
    fontWeight: 'bold',
    textAlign: 'left',
  },
  categoriesText: {
    fontSize: 23,
    color: 'black',
    textAlign: 'left',
    fontWeight: 'bold',
    paddingBottom:10,

  },
  welcomeDescription: {
    fontSize: 17,
    color: 'rgba(96,100,109, 1)',
    textAlign: 'left',
  },
  tutorButton: {
    backgroundColor: 'rgba(255,71,105, 1)',
    alignItems: 'stretch',
    borderRadius: 10,
    marginTop: 5,
  },
  tutorButtonText: {
    fontSize: 17,
    fontWeight: 'bold',
    color: 'white',
    textAlign: 'center',
    padding: 15,
  },
  categoriesContainer: {
    paddingTop: 10,
    paddingBottom:10,
  },
  categoriesCard: {
    borderRadius: 10,
    width: 150,
    height: 150,
    resizeMode:'contain',
    flex: 1,

  },
  getStartedContainer: {
    alignItems: 'center',
    marginHorizontal: 50,
  },
  homeScreenFilename: {
    marginVertical: 7,
  },
  codeHighlightText: {
    color: 'rgba(96,100,109, 0.8)',
  },
  codeHighlightContainer: {
    backgroundColor: 'rgba(0,0,0,0.05)',
    borderRadius: 3,
    paddingHorizontal: 4,
  },
  getStartedText: {
    fontSize: 17,
    color: 'rgba(96,100,109, 1)',
    lineHeight: 24,
    textAlign: 'left',
  },
  tabBarInfoContainer: {
    position: 'absolute',
    bottom: 0,
    left: 0,
    right: 0,
    ...Platform.select({
      ios: {
        shadowColor: 'black',
        shadowOffset: { width: 0, height: -3 },
        shadowOpacity: 0.1,
        shadowRadius: 3,
      },
      android: {
        elevation: 20,
      },
    }),
    alignItems: 'center',
    backgroundColor: '#fbfbfb',
    paddingVertical: 20,
  },
  tabBarInfoText: {
    fontSize: 17,
    color: 'rgba(96,100,109, 1)',
    textAlign: 'center',
  },
  navigationFilename: {
    marginTop: 5,
  },
  helpContainer: {
    marginTop: 15,
    alignItems: 'center',
  },
  helpLink: {
    paddingVertical: 15,
  },
  helpLinkText: {
    fontSize: 14,
    color: '#2e78b7',
  },
  filterButtons: {
    padding: 3,
    backgroundColor: '#F3F3F3',
    borderColor: '#F3F3F3',
    borderRadius: 20,
    borderWidth: 1,
  },
  filterButtonsText: {
    padding:5,
    paddingLeft: 25,
    paddingRight: 25,
  },


});
